mapper.insert:
insert into review(rno, mno, regdate, spoint, comments, fmno)
values(
		 review_no.nextval
		,#{mno:INTEGER}
		,sysdate
		,#{spoint:INTEGER}
		,#{comments:VARCHAR}
		,#{fmno:INTEGER}
)

mapper.update:
update review set mno=#{mno:INTEGER}, regdate=sysdate
				, spoint=#{spoint:INTEGER}, comments=#{comments:VARCHAR}
				, fmno=#{fmno:INTEGER}
where rno=#{rno:INTEGER}

mapper.select:
SELECT * FROM (SELECT rno rno, spoint, COMMENTs, a.fno fno, a.fname fname, a.fmno fmno, TO_CHAR(a.fmdate, 'yyyy-mm-dd') regdate FROM REVIEW r, (select f.FNO, f.FNAME, fm.FMNO, fm.FMDATE FROM FOOD f, FOODMENU fm WHERE f.FNO = fm.FNO) a WHERE a.fmno = r.FMNO)
rno=#{word}
spoint=#{word}
fname like '%'||#{word}||'%'
comments like '%'||#{word}||'%'
order by rno desc

SELECT count(*) FROM (SELECT rno rno, spoint, COMMENTs, a.fno fno, a.fname fname, a.fmno fmno, TO_CHAR(a.fmdate, 'yyyy-mm-dd') regdate FROM REVIEW r, (select f.FNO, f.FNAME, fm.FMNO, fm.FMDATE FROM FOOD f, FOODMENU fm WHERE f.FNO = fm.FNO) a WHERE a.fmno = r.FMNO)
rno=#{word}
spoint=#{word}
fname like '%'||#{word}||'%'
comments like '%'||#{word}||'%'
